import React, {Component} from 'react';
import AppBar from '@material-ui/core/AppBar';
import Button from '@material-ui/core/Button';
import CameraIcon from '@material-ui/icons/PhotoCamera';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Link from '@material-ui/core/Link';
import absoluteUrl from 'next-absolute-url';
import { useRouter } from 'next/router'
import {fs} from "fs-js";
import path from 'path';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const classes = makeStyles((theme) => ({
  icon: {
    marginRight: theme.spacing(2),
  },
  heroContent: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(8, 0, 6),
  },
  heroButtons: {
    marginTop: theme.spacing(4),
  },
  cardGrid: {
    paddingTop: theme.spacing(8),
    paddingBottom: theme.spacing(8),
  },
  card: {
    height: '100%',
    display: 'flex',
    flexDirection: 'column',
  },
  cardMedia: {
    paddingTop: '56.25%', // 16:9
  },
  cardContent: {
    flexGrow: 1,
  },
  footer: {
    backgroundColor: theme.palette.background.paper,
    padding: theme.spacing(6),
  },
}));
  

export default class Resources extends React.Component {

    static async getInitialProps({query}) {


      var Paved = {
          "name" : "Paved", "description" : "Email marketing and sponsored content, simplified. Find newsletters with subscriber profiles that fit your needs, and advertise with them.",
          "shortDescription" : "Email marketing and sponsored content, simplified. Find newsletters with subscriber profiles that…",
          "Likes" : "1",
          "imgURL" : "https://y2u.be/img/why_y2ube_v2.png",
          "URL" : "https://www.paved.com"
      }
      cards[0] = Paved;
      cards[1] = Paved;
      cards[2] = Paved;
      cards[3] = Paved;
      cards[4] = Paved;

      console.log("here **************-->");
      var prefix =  query.category.toString() + query.subcategory.toString();

      // In newer Node.js versions where process is already global this isn't necessary.
      var process = require("process");
      var moveFrom = "/static/Resources/" + prefix;      
      // Loop through all the files in the temp directory
      fs.readdir(moveFrom, function (err, files) {
        if (err) {
          console.error("Could not list the directory.", err);
          process.exit(1);
        }
      
        files.forEach(function (file, index) {
          // Make one pass and make the file complete
          var fromPath = path.join(moveFrom, file);
      
          fs.stat(fromPath, function (error, stat) {
            if (error) {
              console.error("Error stating file.", error);
              return;
            }
      
            if (stat.isFile()) {
              console.log("'%s' is a file.", fromPath);
            } else if (stat.isDirectory()) {
              console.log("'%s' is a directory.", fromPath);
            }
          });
        });
      });
      


      var cards = [];
      return {repos:cards};
    }

    render() {
        return (
          <React.Fragment>
          <CssBaseline />
          <AppBar position="relative">
          <Toolbar>
              <CameraIcon className={classes.icon} />
              <Typography variant="h6" color="inherit" noWrap>
              Album layout
              </Typography>
          </Toolbar>
          </AppBar>
          <main>
          <br></br>
          <br></br>
          <Container className={classes.cardGrid} maxWidth="md">
              {/* End hero unit */}
              <Grid container spacing={4}>
              {this.props.repos.map(card => (
                  <Grid item key= {card.name} xs={12} sm={6} md={4}>
                  <Card className={classes.card}>
                      <CardMedia
                          style = {{ height: 10, paddingTop: '56%'}}
                          className={classes.cardMedia}
                          image= {card.imgURL}
                          title="Image title"
                      />
                      <CardContent className={classes.cardContent}>
                      <Typography gutterBottom variant="h5" component="h2">
                          {card.name}
                      </Typography>
                      <Typography>
                          {card.shortDescription}
                      </Typography>
                      </CardContent>
                      <CardActions>
                      <Button size="small" color="primary">
                          View
                      </Button>
                      <Button size="small" color="primary">
                          Edit
                      </Button>
                      </CardActions>
                  </Card>
                  </Grid>
              ))}
              </Grid>
          </Container>
          </main>
          {/* Footer */}
          <br></br>
          <footer className={classes.footer}>
          <Typography variant="h6" align="center" gutterBottom>
              Footer
          </Typography>
          <Typography variant="subtitle1" align="center" color="textSecondary" component="p">
              Something here to give the footer a purposes!
          </Typography>
          <Copyright />
          </footer>
          {/* End footer */}
      </React.Fragment>
        )
    } 
}
