const firebase = require('firebase/app');







export default function Login() {
	//var uploader = document.getElementById('uploader');
//	var fileButton = document.getElementById('fileButton');
//	
//	
//	//Listen for file selection
//	
	
	

  return (
		<div>
	 			<div className="box">
	 	
	 				<div className="field">
	 				  <div className="file is-info has-name">
	 				    <label className="file-label">
	 				      <input id='fileButton' className="file-input" type="file" name="picture"></input>
	 				      <span className="file-cta">
	 				        <span className="file-icon">
	 				          <i className="fas fa-upload"></i>
	 				        </span>
	 				        <span className="file-label">
	 				          This one --->
	 				        </span>
	 				      </span>
	 				      <span id='fileButton' className="file-name">
	 				      	
	 				      </span>
	 				    </label>
	 				  </div>
	 				</div>
	 				
	 				<figure className="image is-128x128">
	 				  <img className= "image-6" src="https://bulma.io/images/placeholders/256x256.png"></img>
	 				</figure>
	 			</div>
	 			
	 	</div>
	);
}


async function userLogin(req) {

	var fileButton = document.getElementById('fileButton');
	
	fileButton.addEventListener('change', function(e) {
//		// Get file
		
		console.log('here----->');
		const file = e.target.file[0];
		
		// Create storage ref
		const storageRef = firebase.storage().ref('sweet_gifs/' + file.name);
		
		// Upload file
		var task = storageRef.put(file);
		// Update progress bar
		task.on('state_changed',
			function progress(snapshot) {
				var percentage = (snapshot.bytesTransferred / 
				snapshot.totalBytes) * 100;
				uploader.value = percentage;
				console.log('uploading');
			},
			
			function error(err) {
				console.log(err);
			},
			
			function complete() {
				console.log('Completed file upload');
			}
		)
	});
	
	
}
