function uploadPhoto(fileButton, pic, progBar) {

	console.log('here ----------->');
	
	var fileButton = document.getElementById(fileButton);
	var pic_1 = document.getElementById(pic);
	var progBar_1 = document.getElementById(progBar);
	
	
	fileButton.addEventListener('change', function(e) {
		// Get file
		if(e.target.files[0]) {

			console.log('here----->');
			
			var file = e.target.files[0];
			console.log(file.name);
			console.log(file);
	
			//const file = e.target.file[0];
			
			// Create storage ref
			const storageRef = storage.ref('sweet_gifs/' + file.name);
			
			// Upload file
			var task = storageRef.put(file);
			
			//pic_1.src = file.name;
			
						
			// Update progress bar
			task.on('state_changed',
				function progress(snapshot) {
					//progBar_1.style.display = 'initial';

					var percentage = (snapshot.bytesTransferred / 
					snapshot.totalBytes) * 100;
					progBar_1.value = percentage;
					console.log('uploading');
				},
				
				function error(err) {
					console.log(err);
				},
				
				function complete() {
					console.log('Completed file upload');
					
					// Load the image for the user
					storageRef.getDownloadURL().then(function(url) {
						pic_1.src = url;
						progBar_1.style.display = 'none';

					}).catch(function(error){
						console.log(error);
					});
				}
			);
				
		} else {
			console.log('file is null');
			console.log(e.target.files[0]);
		}
	});
}
	
	
