import { Button, FormGroup, FormControl, FormLabel } from "react-bootstrap";
import "../styles/Login.scss";

export default class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: ""
    };
  }

  validateForm() {
    return this.state.email.length > 0 && this.state.password.length > 0;
  }

